sys_ui_action.script:
/* Source https://servicenowguru.com/system-ui/ui-actions-system-ui/client-server-code-ui-action/ */

function escalateToServiceDesk()
{
    var answer = confirm('Are you sure you want to create a child task that will notify Service Desk about this issue?');

    if(answer == false)
    {
		//Abort submission
        return false;
    }
    else
    {
		// Call the UI Action and skip the 'onclick' function
        gsftSubmit(null, g_form.getFormElement(), 'create_inc_task_for_sd');
    }
}
// Code that runs without 'onclick'
// Ensure call to server-side function with no browser errors
if(typeof window == 'undefined')
    escalateServiceDesk();
//Server-side function
function escalateServiceDesk()
{
	var intk = new GlideRecord("incident_task");
    intk.initialize();
	//inc.caller_id = current.caller_id;
	//inc.category = current.category;
	//inc.subcategory = current.subcategory;
	intk.business_service = current.business_service;
	intk.service_offering = current.service_offering;
	intk.cmdb_ci = current.cmdb_ci;
	intk.contact_type = current.contact_type;
	//inc.state = current.state;
	//tnc.hold_reason = current.hold_reason;
	intk.impact = current.impact;
	intk.urgency = current.urgency;
	intk.priority = current.priority;
	intk.assignment_group = gs.getProperty('group.Service.Desk');
	intk.short_description = current.short_description;
	intk.description = current.description;
	intk.incident = current.sys_id;
	intk.work_notes = 'This Task was created (cloned) from LMS Incident ' + current.number + ' because further assistance is needed in processing';
	intk.insert();
}
