sys_ui_action.script:
/* Source https://servicenowguru.com/system-ui/ui-actions-system-ui/client-server-code-ui-action/ */

function escalateToServiceDesk()
{
    var answer = confirm('Are you sure you want to escalate this Incident to the Service Desk?');

    if(answer == false)
    {
		//Abort submission
        return false;
    }
    else
    {
		// Call the UI Action and skip the 'onclick' function
        gsftSubmit(null, g_form.getFormElement(), 'lms_escalate_to_service_desk');
    }
}
// Code that runs without 'onclick'
// Ensure call to server-side function with no browser errors
if(typeof window == 'undefined')
    escalateServiceDesk();
//Server-side function
function escalateServiceDesk()
{
	var inc = new GlideRecord("incident");
    inc.initialize();
	inc.caller_id = current.caller_id;
	inc.category = current.category;
	inc.subcategory = current.subcategory;
	inc.business_service = current.business_service;
	inc.service_offering = current.service_offering;
	inc.cmdb_ci = current.cmdb_ci;
	inc.contact_type = current.contact_type;
	//inc.state = current.state;
	//inc.hold_reason = current.hold_reason;
	inc.impact = current.impact;
	inc.urgency = current.urgency;
	inc.priority = current.priority;
	inc.assignment_group = gs.getProperty('group.Service.Desk');
	inc.short_description = current.short_description;
	inc.description = current.description;
	inc.parent_incident = current.sys_id;
	inc.insert();
}
